import React, { Component } from 'react'
import styled from 'styled-components'

const Wrapper = styled.div`
  background: url(${props => props.image});
  grid-row-start: span ${props => props.row};
  grid-column-start: span ${props => props.col};
`

const Img = styled.img`
  width: 100%;
  height: 100%;
  display: block;
  /* object-fit: cover; */
`

class Box extends Component {
  render() {
    console.log(this.props.col)
    console.log(this.props.row)
    return (
      <Wrapper
        row={this.props.row}
        col={this.props.col}
        image={require(`./images/${this.props.image}`)}
      >
        {/* <Img src={require(`./images/${this.props.image}`)} /> */}
      </Wrapper>
    )
  }
}

export default Box
