import React, { Component } from 'react'
import styled from 'styled-components'

// Components
import FullScreen from '../fullscreen'
import Topic from '../topic'
import Box from './box'

const Container = styled.div`
  max-width: 1000px;
  margin: auto;
`

class Skill extends Component {
  render() {
    return (
      <FullScreen>
        <Topic>Skills</Topic>
        <Container>
          {this.props.skills.map((skill, i) => (
            <Box
              key={i}
              title={skill.title}
              percent={skill.percent}
              code={skill.code}
              type={skill.type}
            />
          ))}
          <Box title="Coding" percent={80} code={'\f5fc'} type={'solid'} />
          <Box
            title="Web Development"
            percent={80}
            code={'\f40e'}
            type={'regular'}
          />
          <Box
            title="Data Science"
            percent={40}
            code={'\f643'}
            type={'solid'}
          />
          <Box
            title="Embedded system"
            percent={20}
            code={'\f085'}
            type={'solid'}
          />
        </Container>
      </FullScreen>
    )
  }
}

export default Skill
