import React, { Component } from 'react'
import styled from 'styled-components'

const Wrapper = styled.div`
  text-align: center;
  margin: 0 auto 50px;
  display: flex;
  justify-content: center;
  align-items: center;
`

const Square = styled(Wrapper)`
  background: rgba(175, 205, 247, 0.3);
  width: 100px;
  height: 100px;
`

const Circle = styled(Wrapper)`
  background: rgba(175, 205, 247, 0.3);
  width: 120px;
  height: 120px;
  border-radius: 50%;
`

const Triangle = styled(Wrapper)`
  width: 0;
  height: 0;
  border-left: 60px solid transparent;
  border-right: 60px solid transparent;
  border-bottom: 100px solid rgba(175, 205, 247, 0.3);

  & * {
    position: relative;
    top: 60px;
  }
`

const Text = styled.h1`
  color: #4b7abc;
  text-transform: uppercase;
  text-align: center;
  letter-spacing: 3px;
  margin: 0;
`

class Topic extends Component {
  render() {
    return (
      <React.Fragment>
        {this.props.shape === 'square' ? (
          <h1>dlksjfkld</h1>
        ) : // <Square>
        // <Text>{this.props.children}</Text>
        // </Square>
        null}
        {this.props.shape === 'circle' ? (
          <Circle>
            <Text>{this.props.children}</Text>
          </Circle>
        ) : null}
        {this.props.shape === 'triangle' ? (
          <Triangle>
            <Text>{this.props.children}</Text>
          </Triangle>
        ) : null}
      </React.Fragment>
    )
  }
}

export default Topic
