import React, { Component } from 'react'
import styled from 'styled-components'

const size = 120
const border = 15

const Wrapper = styled.div`
  position: relative;
  width: ${size}px;
  height: ${size}px;
  background-color: #f2e9e1;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;

  &:after {
    border: none;
    position: absolute;
    border-radius: 50%;
    width: ${size - border}px;
    height: ${size - border}px;
    background-color: white;
    content: '';
  }
`

const Font = styled.span`
  color: #53777a;
  z-index: 2;
  font-family: 'Font Awesome Solid';
  font-size: 2rem;

  &:before {
    content: '\f5fc';
  }
`

const Left = styled.div`
  border-radius: 50%;
  width: ${size}px;
  height: ${size}px;
  position: absolute;
  clip: rect(
    0,
    ${size}px,
    ${size}px,
    ${size / 2}px
  ); /* clips the whole left half*/
  clip: rect(auto, auto, auto, auto); /*50% up*/
`

const First = styled.div`
  /*Progress bar for the first 50%, filling the whole right half*/
  position: absolute; /*needed for clipping*/
  clip: rect(0, ${size}px, ${size}px, ${size / 2}px);
  background-color: #53777a;
  border-radius: 50%;
  width: ${size}px;
  height: ${size}px;
`

const Value = styled.div.attrs(props => ({
  percent: (props.percent * 360) / 100
}))`
  position: absolute;
  clip: rect(0, ${size / 2}px, ${size}px, 0);
  width: ${size}px;
  height: ${size}px;
  border-radius: 50%;
  border: ${border}px solid #53777a;
  box-sizing: border-box;
  transform: rotate(${props => props.percent}deg);
`

class Icon extends Component {
  render() {
    return (
      <Wrapper>
        <Font />
        {this.props.percent <= 50 ? (
          <Left>
            <Value percent={this.props.percent} />
          </Left>
        ) : (
          <React.Fragment>
            <First />
            <Value percent={this.props.percent} />
          </React.Fragment>
        )}
      </Wrapper>
    )
  }
}

export default Icon
