import React, { Component } from 'react'
import styled from 'styled-components'
import '../../webfonts/*'

// Components
import FullScreen from '../fullscreen'

// Images
import background from './background.jpg'

const Wrapper = styled(FullScreen)`
  color: white;
  background: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)),
    url(${background});
  background-size: cover;
  width: 70%;
  display: inline-block;
  vertical-align: top;
`

const Container = styled.div`
  width: intrinsic;
  padding: 30px 50px;
  position: relative;
`

const Border = styled.div`
  &:before {
    content: '';
    position: absolute;
    width: 70%;
    height: 60%;
    bottom: 0;
    left: 0;
    border-bottom: 1px solid rgba(255, 255, 255, 0.6);
    border-left: 1px solid rgba(255, 255, 255, 0.6);
  }

  &:after {
    content: '';
    position: absolute;
    width: 70%;
    height: 60%;
    top: 0;
    right: 0;
    border-top: 1px solid rgba(255, 255, 255, 0.6);
    border-right: 1px solid rgba(255, 255, 255, 0.6);
  }
`

const Title = styled.h1`
  color: white;
  font-family: 'Julius Sans One', sans-serif;
  font-size: 3.5rem;
  margin: 0 0 10px;
`

const Detail = styled.p`
  font-size: 0.9rem;
  letter-spacing: 3px;
  margin: 0 0 0 5px;
  opacity: 0.6;
`

const Education = styled.p`
  font-size: 0.8rem;
  margin: 15px 20px 0 5px;
  opacity: 0.6;
  display: inline-block;

  &:before {
    font-family: 'Font Awesome 5 Pro';
    content: '\f19d';
    margin-right: 7px;
  }
`

class Info extends Component {
  render() {
    const birthday = new Date('1996/12/26')
    const age = Math.floor((Date.now() - birthday) / 31556926000)

    return (
      <Wrapper>
        <Container>
          <Border />
          <Title>punn</Title>
          <Detail>Punchaya Siriphanthong</Detail>
          <Detail>26 December 1996 | {age} years old</Detail>
          <Detail>p.siriphanthong@gmail.com</Detail>
          {this.props.educations.map((education, i) => (
            <Education key={i}>{education}</Education>
          ))}
        </Container>
      </Wrapper>
    )
  }
}

export default Info
