import React, { Component } from 'react'
import styled from 'styled-components'

const Wrapper = styled.div`
  width: 100px;
  height: 100px;
  background: rgba(175, 205, 247, 0.3);
  text-align: center;
  margin: 0 auto 50px;
  display: flex;
  justify-content: center;
  align-items: center;
  ${props =>
    props.shape === 'triangle' &&
    `

      `}

  & * {
    ${props =>
      props.shape === 'triangle' &&
      `
        position: relative;
        top: 60px;`}
  }
`

const Circle = styled(Wrapper)`
  width: 120px;
  height: 120px;
  border-radius: 50%;
`

const Triangle = styled(Wrapper)`
  width: 0;
  height: 0;
  background: transparent;
  border-left: 60px solid transparent;
  border-right: 60px solid transparent;
  border-bottom: 100px solid rgba(175, 205, 247, 0.3);

  width: 0;
  height: 0;
  background: transparent;
  border-left: 60px solid transparent;
  border-right: 60px solid transparent;
  border-bottom: 100px solid rgba(175, 205, 247, 0.3);
`

const Text = styled.h1`
  color: #4b7abc;
  text-transform: uppercase;
  text-align: center;
  letter-spacing: 3px;
  margin: 0;
`

class Topic extends Component {
  render() {
    return (
      <React.Fragment>
        {this.props.shape === 'circle' ? (
          <Circle shape={this.props.shape}>
            <Text>{this.props.children}</Text>
          </Circle>
        ) : null}
      </React.Fragment>
    )
  }
}

export default Topic
